import React, { useEffect, useState } from "react"
import axios from "axios"
import CreateNotes from "./CreateNotes"

function Notes(props) {
  const posts = props.post
  const [notes, updateNotes] = useState([])
  const [fetchNotes, updateFetchNotes] = useState(false)
  useEffect(() => {
    const apiCall = async () => {
      await axios.get(
        "https://api.airtable.com/v0/app7JCVhsKVPmHS6X/Table%201?view=Grid%20view",
        {
          headers: {
            Authorization: `Bearer ${process.env.REACT_APP_AIRTABLE_KEY}`,
          },
        }
      )
      updateNotes(posts.data.records)
    }
    apiCall()
  }, [fetchNotes])
  return (
    <div>
      <CreateNotes
        updateFetchMovieInfo={updateFetchNotes}
        fetchBlogPosts={fetchNotes}
      />
    </div>
  )
}

export default Notes
