import React, { useEffect, useState } from "react"
import axios from "axios"

import PostActors from "./PostActors"
import CreateActors from "./CreateActors"

function Actors() {
  //   const [blogPosts, updateBlogPosts] = useState([])
  //   const [fetchBlogPosts, updateFetchBlogPosts] = useState(false)

  useEffect(() => {
    const apiCall = async () => {
      const posts = await axios.get(
        "https://api.airtable.com/v0/app7JCVhsKVPmHS6X/Table%201?view=Grid%20view",
        {
          headers: {
            Authorization: `Bearer ${process.env.REACT_APP_AIRTABLE_KEY}`,
          },
        }
      )
      updateBlogPosts(posts.data.records)
    }
    apiCall()
  }, [fetchBlogPosts])

  return (
    <main>
      {/* <CreateActors
        updateFetchBlogPosts={updateFetchBlogPosts}
        fetchBlogPosts={fetchBlogPosts}
      /> */}
      <label htmlFor="actor">Actor</label>
      <input
        name="text"
        id="actor"
        onChange={(e) => updateName(e.target.value)}
      ></input>
      <label htmlFor="movie">Movie</label>
      <input
        name="text"
        id="movie"
        onChange={(e) => updateCast(e.target.value)}
      ></input>
      {blogPosts.map((post) => (
        <PostActors
          post={post}
          key={post.id}
          fetchBlogPosts={fetchBlogPosts}
          updateFetchBlogPosts={updateFetchBlogPosts}
        />
      ))}
    </main>
  )
}

export default Actors
