import React from "react"
import { Route, Link } from "react-router-dom"
import Actors from "./Actors"

function Home() {
  const [blogPosts, updateBlogPosts] = useState([])
  const [fetchBlogPosts, updateFetchBlogPosts] = useState(false)

  useEffect(() => {
    const apiCall = async () => {
      const posts = await axios.get(
        "https://api.airtable.com/v0/appFhr7txjsns0HMI/Table%201?view=Grid%20view",
        {
          headers: {
            Authorization: `Bearer ${process.env.REACT_APP_AIRTABLE_KEY}`,
          },
        }
      )
      updateBlogPosts(posts.data.records)
    }
    apiCall()
  }, [fetchBlogPosts])

  return (
    <div>
      {/* <Link to="/actors">
        <h1>Home Page!!!</h1>
      </Link>
      <Route path="/actors">
        <Actors />
      </Route> */}
      <main>
        <h1>My Blog</h1>
        <CreatePost
          updateFetchBlogPosts={updateFetchBlogPosts}
          fetchBlogPosts={fetchBlogPosts}
        />
        {blogPosts.map((post) => (
          <Post
            post={post}
            key={post.id}
            fetchBlogPosts={fetchBlogPosts}
            updateFetchBlogPosts={updateFetchBlogPosts}
          />
        ))}
      </main>
    </div>
  )
}

export default Home
