import React, { useState } from "react"
import axios from "axios"

function UpdateActors(props) {
  const [name, updateName] = useState("props.post.fields.name")
  const [cast, updateCast] = useState("props.post.fields.cast")

  const handleSubmit = async (e) => {
    e.preventDefault()
    const data = await axios.put(
      `https://api.airtable.com/v0/app7JCVhsKVPmHS6X/Table%201/rec8OG8tAT71k3IjA/${props.post.id}`,
      {
        fields: {
          name: name,
          cast: cast,
        },
      },
      {
        headers: {
          Authorization: `Bearer ${process.env.REACT_APP_AIRTABLE_KEY}`,
          "Content-Type": "application/json",
        },
      }
    )
    props.updateFetchBlogPosts(!props.fetchBlogPosts)
    updateName("")
    updateCast("")
  }
  return (
    <div>
      <h1>Hello Actors!!</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="actor">Actor</label>
        <input
          name="text"
          id="actor"
          onChange={(e) => updateName(e.target.value)}
        ></input>
        <input type="submit" value="Submit Actors-List" />
      </form>
    </div>
  )
}

export default UpdateActors
