import React from "react"
import UpdateActors from "./UpdateActors"
import "./App.css"

function PostActors(props) {
  const post = props.post

  return (
    <div>
      <section className="form-container">
        <h2 className="first-header">{post.fields.name}</h2>
        <h2 className="second-header">{post.fields.movie}</h2>
        <img src={post.fields.image} alt="img" className="image-tag" />
      </section>
      <UpdateActors
        post={post}
        fetchMovieInfo={props.fetchMovieInfo}
        updateFetchMovieInfo={props.updateFetchMovieInfo}
      />
    </div>
  )
}

export default PostActors
