import React, { useEffect, useState } from "react"
import axios from "axios"
import CreateNotes from "./CreateNotes"

function Notes() {
  const [notes, updateNotes] = useState([])
  const [fetchNotes, updateFetchNotes] = useState(false)
  useEffect(() => {
    const apiCall = async () => {
      const posts = await axios.get(
        "https://api.airtable.com/v0/app7JCVhsKVPmHS6X/Table%201?view=Grid%20view",
        {
          headers: {
            Authorization: `Bearer ${process.env.REACT_APP_AIRTABLE_KEY}`,
          },
        }
      )
      updateNotes(posts.data.records)
    }
    apiCall()
  }, [fetchNotes])
  return (
    <div>
      <h1>My Notes!!</h1>
      <form>
        <label htmlFor="actor">Notes</label>
        <textarea name="text" id="text" cols="30" rows="10"></textarea>
        <input type="submit" value="Submit Notes" />
      </form>
    </div>
  )
}

export default Notes
