import React, { useState } from "react"
import axios from "axios"

function Actors(props) {
  const [name, updateName] = useState("")
  const [cast, updateCast] = useState("")

  const handleSubmit = async (e) => {
    e.preventDefault()
    const data = await axios.post(
      "https://api.airtable.com/v0/app7JCVhsKVPmHS6X/Table%201 ",
      {
        fields: {
          name: name,
          cast: cast,
        },
      },
      {
        headers: {
          Authorization: `Bearer ${process.env.REACT_APP_AIRTABLE_KEY}`,
          "Content-Type": "application/json",
        },
      }
    )
    props.updateFetchBlogPosts(!props.fetchBlogPosts)
    updateName("")
    updateCast("")
  }
  return (
    <div>
      <h1>Hello Actors!!</h1>
      <form onSubmit={handleSubmit}>
        <label htmlFor="actor">Actor</label>
        <input
          name="text"
          id="cast"
          cols="30"
          rows="10"
          onChange={(e) => updateCast(e.target.value)}
        ></input>
        <input type="submit" value="Submit Actors-List" />
      </form>
    </div>
  )
}

export default Actors
