import React, { useEffect, useState } from "react"
import { Route, Link } from "react-router-dom"
import Actors from "./Actors"
import axios from "axios"

function Home() {
  const [blogPosts, updateBlogPosts] = useState([])
  const [fetchBlogPosts, updateFetchBlogPosts] = useState(false)

  useEffect(() => {
    const apiCall = async () => {
      const posts = await axios.get(
        "https://api.airtable.com/v0/app7JCVhsKVPmHS6X/Table%201?maxRecords=3&view=Grid%20view",
        {
          headers: {
            Authorization: `Bearer ${process.env.REACT_APP_AIRTABLE_KEY}`,
          },
        }
      )
      updateBlogPosts(posts.data.records)
    }
    apiCall()
  }, [fetchBlogPosts])
  return (
    <div>
      <Link to="/actors">
        <h1>Home Page!!!</h1>
      </Link>
      <Route path="/actors">
        <Actors />
      </Route>

      <main>
        <h1>My Blog</h1>
        <Actors
          updateFetchBlogPosts={updateFetchBlogPosts}
          fetchBlogPosts={fetchBlogPosts}
        />
        {/* {blogPosts.map((post) => (
          <Post
            post={post}
            key={post.id}
            fetchBlogPosts={fetchBlogPosts}
            updateFetchBlogPosts={updateFetchBlogPosts}
          />
        ))} */}
      </main>
    </div>
  )
}

export default Home
