import React, { useState } from "react"
import axios from "axios"

function UpdateNotes(props) {
  const [name, updateName] = useState(props.post.fields.name)
  const [notes, updateNotes] = useState(props.post.fields.notes)

  const handleSubmit = async (e) => {
    e.preventDefault()
    await axios.put(
      `https://api.airtable.com/v0/app7JCVhsKVPmHS6X/Table%201/${post.id}`,
      {
        fields: {
          name: name,
          notes: notes,
        },
      },
      {
        headers: {
          Authorization: `Bearer ${process.env.REACT_APP_AIRTABLE_KEY}`,
          "Content-Type": "application/json",
        },
      }
    )
    props.updateFetchNoteInfo(!props.fetchNoteInfo)
    updateName("")
    updateNotes("")
  }

  return (
    <div>
      <form onClick={handleSubmit}>
        <label htmlFor="name">Name</label>
        <input
          name="text"
          id="name"
          onChange={(e) => updateName(e.target.value)}
        />
        <label htmlFor="notes">Notes</label>
        <textarea
          name="text"
          id="notes"
          cols="30"
          rows="10"
          onChange={(e) => updateNotes(e.target.value)}
        ></textarea>
        <input type="submit" value="Submit Notes" />
      </form>
    </div>
  )
}
export default UpdateNotes
